var uri = require('url')
  , util = require('util')
  , OAuth2Strategy = require('passport-oauth2')

baseResourceUri = 'https://api.uber.com/v1/'
baseOAuthUri = 'https://login.uber.com/'

function Strategy(options, verify) {
  options = options || {};
  options.authorizationURL = options.authorizationURL || uri.resolve(baseOAuthUri, '/oauth/authorize');
  options.tokenURL = options.tokenURL || uri.resolve(baseOAuthUri, 'oauth/token');
  options.scopeSeparator = options.scopeSeparator || ' ';

  OAuth2Strategy.call(this, options, verify);

  this.name = 'uber';
}

util.inherits(Strategy, OAuth2Strategy);

Strategy.prototype.userProfile = function(accessToken, done) {
  this._oauth2.get(uri.resolve(baseResourceUri, 'me'), accessToken, function (err, body, res) {
    var profile = {};
    try {
      profile = JSON.parse(body);
    } catch (ex) {
      return done(new Error('Failed to parse user profile'));
    }

    profile.provider = 'uber';
    done(null, profile);
  });
};

module.exports = Strategy;
